import React, { useState } from "react";
import "./TodoAdd.css";
import { FaPlusCircle } from "react-icons/fa";

export function TodoAdd(props) {
  const [newTodoValue, setNewTodoValue] = useState("");

  const handleNewTodoValueChange = (e) => {
    setNewTodoValue(e.target.value);
  };
  // onTodoItemSubmit callback from TodoApp.jsx
  const handleSubmitNewValueTodo = () => {
    props.onTodoItemSubmit(newTodoValue);
    setNewTodoValue("");
  };

  return (
    <div className="todo-add">
      <input
        value={newTodoValue}
        onChange={handleNewTodoValueChange}
        type="text"
        className="todo-add-input"
        placeholder="What need to be done?"
      />
      <button className="todo-add-btn" onClick={handleSubmitNewValueTodo}>
        <FaPlusCircle />
        Add
      </button>
    </div>
  );
}
